INSERT INTO `company_infos` VALUES ('65bd8abbb2d7b6f6', '北京圣承方略咨询有限公司', '北京圣承方略咨询有限公司', '私营/民营企业', '北京市朝阳区来广营西路5号院诚盈中心3号楼12层1205单元', '专业服务', '100-499人', '公司简介:\r     圣承方略公司成立于2009年，注册资金1000万，国内唯一一家以咨询培训服务为主体且打通汽车行业线上线下全业务链的集团化咨询公司。总部位于北京，并在上     海广州、武汉、等地设有分公司，\r       圣承方略现有员工人数253人，2017年营业额已突破1.6 亿；\r       专注于为汽车行业提供企业管理咨询整体解决方案，时刻紧跟行业发展趋势，探索和实践B-B-C与O2O的模式相结合的体系，实现将互联网思维融入客户价值链管理业务，助力经销店打造智能化全功能店。\r       业务内容包括企业管理咨询、CRM咨询及IT应用系统开发及运营服务、经销商培训与辅导 等。 \r\r目前服务的品牌:一汽-大众、一汽-大众奥迪、东风日产、一汽集团、一汽红旗、东风小康、斯巴鲁、广汽三菱、广汽丰田、一汽丰田、东风雷诺、英菲尼迪等。   \r', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
