INSERT INTO `job_infos` VALUES ('55a80eb880127d96', 'e7c3755077d40f04', 'Python开发工程师-北京', '杭州迪普科技股份有限公司', '实习', '10-20k', '统招本科', '1-3年', '北京-西北旺', NULL, '工作职责:1、参与公司云平台产品（基于OpenStack）的系统研发；2、根据开发计划，负责公司基于OpenStack的IaaS/PaaS/SaaS云平台功能模块的详细设计、开发和单元测试工作；3、解决部署实施过程遇到的技术问题。', '任职资格:1、熟悉Python，有1年以上系统开发和设计经验；2、熟悉Django开发框架3、熟悉Linux系统，熟悉网络原理；4、熟悉OpenStack ，CloudStack等云计算开源项目，有云计算的项目开发及运维经验者优先；以上要求至少满足两种5、本科及以上学历，计算机、通信、电子或相关专业；6、英语四级以上。', NULL, NULL);
