INSERT INTO `job_infos` VALUES ('efaee5a9378ba91e', 'c9945fe4260aa2c3', 'webgis开发工程师', '皓行科技', '应届', '30-50k', '本科及以上', '1-3年', '北京', NULL, '职责描述:1、能够根据设计文档完成功能分解，并完成功能详细设计；2、使用web开发相关技术独立完成功能模块的编码工作；3、完成功能模块基础功能测试及bug修改；4、按计划完成任务，并能及时反馈进度与问题；5、配合项目经理完成系统的需求分析和设计工作，并能够按要求编写需求分析和系统设计文档。', '任职要求:1、计算机或地理信息系统相关专业；2、熟悉ArcGIS系统常用GIS软件及二次开发；3、至少熟悉一门开发语言（C#、Java）；4、熟悉WEB前端开发技术，包括HTML、CSS+DIV、ElementUI、JavaScript、Ajax等相关内容，熟悉Vue、Jquery开发框架；5、具有良好的沟通能力、学习能力和团队合作意识、良好的职业素养和责任心；6、熟悉MySQL或PostgreSQL等开源数据库的应用开发。', NULL, NULL);
INSERT INTO `job_infos` VALUES ('203f033309b7ebdd', 'c9945fe4260aa2c3', 'webgis开发工程师', '皓行科技', '实习', '30-50k', '本科及以上', '1-3年', '武汉', NULL, '职责描述:1、能够根据设计文档完成功能分解，并完成功能详细设计；2、使用web开发相关技术独立完成功能模块的编码工作；3、完成功能模块基础功能测试及bug修改；4、按计划完成任务，并能及时反馈进度与问题；5、配合项目经理完成系统的需求分析和设计工作，并能够按要求编写需求分析和系统设计文档。', '任职要求:1、计算机或地理信息系统相关专业；2、熟悉ArcGIS系统常用GIS软件及二次开发；3、至少熟悉一门开发语言（C#、Java）；4、熟悉WEB前端开发技术，包括HTML、CSS+DIV、ElementUI、JavaScript、Ajax等相关内容，熟悉Vue、Jquery开发框架；5、具有良好的沟通能力、学习能力和团队合作意识、良好的职业素养和责任心；6、熟悉MySQL或PostgreSQL等开源数据库的应用开发。', NULL, NULL);
INSERT INTO `job_infos` VALUES ('96b9c596611dac61', 'c9945fe4260aa2c3', '大数据开发工程师', '皓行科技', '应届', '30-50k', '本科及以上', '1-3年', '北京', NULL, '职责描述:1、搭建和维护Hadoop集群，基于Hadoop对数据进行加工整合、深度分析，并将数据整合成数据产品，提供数据服务；2、负责分布式大数据平台数据建模和数据应用后端开发；3、负责大数据相关组件OLAP引擎、实时计算，文件存储等相关研究和开发；4、负责大数据系统及组件的性能、效率、稳定性分析与优化；5、负责根据业务场景，项目需求抽取、分析处理。', '任职要求:1、计算机、数学或统计、模式识别等相关专业，硕士以上学历，两年以上文本分析、文本分类相关工作经验；2、掌握机器学习/深度学习领域的基础理论和方法，有该领域算法的研究或开发经验；3、在深度学习领域有较深的理论研究和实践经验，掌握其模型及应用方法，如DNN、CNN、RNN、LSTM、FCN、GAN、DRN 、SVM等具有深入理解，有图像或文本处理相关领域经验者优先；4、熟悉openCV,熟悉Python爬虫并有相关经验者优先；5、熟练使用以下任何两种语言:Python/R/Java，熟悉linux，且有较强的编程能力；6、熟悉 Hadoop、Spark，有过基于GPU指令的算法优化经验者优先；7、具有较好的团队合作精神、沟通能力和较强的责任感。\"=', NULL, NULL);
